namespace Tests {
  using System.IO;
  using System.Diagnostics.Contracts;

  using Aegis_DVL;
  using Aegis_DVL.Cryptography;
  using Aegis_DVL.Data_Types;
  using Aegis_DVL.Util;

  using Org.BouncyCastle.Crypto;

  using NUnit.Framework;

  /// <summary>
  /// The crypto tests.
  /// </summary>
  [TestFixture] public class CryptoTests {
    #region Fields

    /// <summary>
    /// The _station.
    /// </summary>
    private Station _station;

    #endregion

    #region Public Properties

    /// <summary>
    /// Gets the crypto.
    /// </summary>
    public ICrypto Crypto { get; private set; }

    #endregion

    #region Public Methods and Operators

    [Test] public void AsymmetricTests() {
      AsymmetricTest("");
      AsymmetricTest("0");
      AsymmetricTest("\n");
      AsymmetricTest("1234567890");
      // 53 when encoded
      AsymmetricTest("12345678901234567890123456789");
      // 54 when encoded
      AsymmetricTest("123456789012345678901234567890");
      // 511
      AsymmetricTest("");
      // 512
      AsymmetricTest("");
      // 513
      AsymmetricTest("");
      // 1024
      AsymmetricTest("");
      AsymmetricTest("Howdy there, partner!");
    }

    /// <summary>
    /// Test asymmetric crypto features of Crypto subsystem.
    /// </summary>
    public void AsymmetricTest(string testString) {
      Contract.Requires(testString != null);
      // Encrypt/decrypt
      byte[] bytes = Bytes.From(testString);
      CipherText ciphertext = Crypto.AsymmetricEncrypt(bytes, 
        new AsymmetricKey(Crypto.KeyPair.Public));
      Assert.That(!bytes.IsIdenticalTo(ciphertext));
      byte[] decryptedBytes = Crypto.AsymmetricDecrypt(ciphertext, 
        new AsymmetricKey(Crypto.KeyPair.Private));
      Assert.That(bytes.IsIdenticalTo(decryptedBytes));
      Assert.That(decryptedBytes.To<string>().Equals(testString));
    }

    /// <summary>
    /// The generate password test.
    /// </summary>
    [Test] public void GeneratePasswordTest() {
      string pswd = Aegis_DVL.Cryptography.Crypto.GeneratePassword();
      Assert.That(!string.IsNullOrEmpty(pswd));
    }

    /// <summary>
    /// The hash tests.
    /// </summary>
    [Test] public void HashTests() { 
      Assert.That(HashTest("", ""));
      Assert.That(HashTest("", " "));
      Assert.That(HashTest(" ", " "));
      Assert.That(HashTest("0", "O"));
      Assert.That(HashTest("0", "0"));
      Assert.That(HashTest("1234567890", "1234567890"));
      Assert.That(HashTest("1234567890", " 1234567890"));
      // 1024 bytes before encoding
      Assert.That(HashTest("", ""));
      Assert.That(HashTest("", " "));
    }

    /// <summary>
    /// Hash the two inputs.  If they are identical, they must have identical
    /// hashes, otherwise they should not.
    /// </summary>
    public bool HashTest(string s, string t) {
      Contract.Requires(s != null);
      Contract.Requires(t != null);
      byte[] hashS = Crypto.Hash(Bytes.From(s));
      byte[] hashT = Crypto.Hash(Bytes.From(t));
      if (s.Equals(t))
        return hashS.Equals(hashT);
      else
        return !hashS.Equals(hashT);
    }

    /// <summary>
    /// The iv test.
    /// </summary>
    [Test] public void IvTests() {
      // first obvious simple tests for generation and assignment
      byte[] oldIv = Crypto.Iv;
      Crypto.NewIv();
      Assert.That(!oldIv.IsIdenticalTo(Crypto.Iv));
      Crypto.Iv = oldIv;
      Assert.That(oldIv.IsIdenticalTo(Crypto.Iv));
      // probabalistic tests
      float mean = 0;
      long sum = 0;
      for (int i = 0; i < 1000; i++) {
        byte[] iv = Crypto.Iv;
        Crypto.NewIv();
        Assert.That(!iv.IsIdenticalTo(Crypto.Iv));
        for (int index = 0; index < Crypto.Iv.Length; index++)
          sum += Crypto.Iv[index];
      }
      mean = sum / (1000 * Crypto.Iv.Length);
    }

    /// <summary>
    /// The symmetric test.
    /// </summary>
    [Test] public void SymmetricTest() {
      var key = new SymmetricKey(Crypto.GenerateSymmetricKey());
      const string TestString = "Howdy there, partner!";
      byte[] bytes = Bytes.From(TestString);
      CipherText ciphertext = Crypto.SymmetricEncrypt(bytes, key);
      Assert.That(!bytes.IsIdenticalTo(ciphertext));
      byte[] decryptedBytes = Crypto.SymmetricDecrypt(ciphertext, key);
      Assert.That(bytes.IsIdenticalTo(decryptedBytes));
      Assert.That(decryptedBytes.To<string>().Equals(TestString));
    }

    /// <summary>
    ///   SetUp test helper properties.
    /// </summary>
    [SetUp]
    public void SetUp() {
      _station = new Station(new TestUi(), SystemTestData.Key, SystemTestData.Password,
        SystemTestData.StationPort, "CryptoTestVoters.sqlite");
      Crypto = _station.Crypto;
    }

    /// <summary>
    /// The tear down.
    /// </summary>
    [TearDown] public void TearDown() {
      _station.Dispose();
      _station = null;
      File.Delete("CryptoTestVoters.sqlite");
    }

    #endregion
  }
}
